package org.kompiro.jamcircle.web;import org.kompiro.jamcircle.kanban.model.Board;import com.vaadin.Application;import com.vaadin.data.Container;import com.vaadin.ui.*;public class JAMCircleWebApplication extends Application {	private static final long serialVersionUID = 1L;	@Override	public void init() {		Window mainWindow = new Window("JAM Circle");		Board[] boards = WebContext.getDefault().getKanbanService().findAllBoard();		Tree tree = new Tree("Board");		Container newDataSource = new BoardHierarchy(boards);		tree.setContainerDataSource(newDataSource);		tree.setItemCaptionMode(AbstractSelect.ITEM_CAPTION_MODE_PROPERTY);		tree.setItemCaptionPropertyId("caption");		mainWindow.addComponent(tree);		setMainWindow(mainWindow);	}}